import{_ as t,c as o,o as l,ag as n}from"./chunks/framework.BmLMQRXF.js";const c=JSON.parse('{"title":"科研","description":"","frontmatter":{},"headers":[],"relativePath":"coding/科研.md","filePath":"coding/科研.md"}'),a={name:"coding/科研.md"};function s(i,r,e,g,h,u){return l(),o("div",null,r[0]||(r[0]=[n('<h1 id="科研" tabindex="-1">科研 <a class="header-anchor" href="#科研" aria-label="Permalink to &quot;科研&quot;">​</a></h1><h3 id="一、论文的层次分类" tabindex="-1"><strong>一、论文的层次分类</strong> <a class="header-anchor" href="#一、论文的层次分类" aria-label="Permalink to &quot;**一、论文的层次分类**&quot;">​</a></h3><h4 id="_1-顶级会议-期刊-顶会顶刊" tabindex="-1"><strong>1. 顶级会议/期刊（顶会顶刊）</strong> <a class="header-anchor" href="#_1-顶级会议-期刊-顶会顶刊" aria-label="Permalink to &quot;**1. 顶级会议/期刊（顶会顶刊）**&quot;">​</a></h4><ul><li><strong>特点</strong>：国际公认的权威平台，录用率低（10%~25%），学术影响力大，评审严格。</li><li><strong>计算机领域举例</strong>： <ul><li><strong>AI/机器学习</strong>：NeurIPS、ICML、CVPR、ICLR、AAAI</li><li><strong>系统/网络</strong>：SIGCOMM、OSDI、SOSP、NSDI</li><li><strong>数据库</strong>：SIGMOD、VLDB</li><li><strong>理论计算机</strong>：STOC、FOCS</li></ul></li><li><strong>意义</strong>：发表顶会论文是学术能力的强力证明，对申请博士或求职有显著帮助。</li></ul><h4 id="_2-二类会议-期刊-优质但非顶尖" tabindex="-1"><strong>2. 二类会议/期刊（优质但非顶尖）</strong> <a class="header-anchor" href="#_2-二类会议-期刊-优质但非顶尖" aria-label="Permalink to &quot;**2. 二类会议/期刊（优质但非顶尖）**&quot;">​</a></h4><ul><li><strong>特点</strong>：学科内知名平台，录用率适中（20%~40%），适合阶段性成果展示。</li><li><strong>举例</strong>： <ul><li><strong>AI领域</strong>：ACL（计算语言学）、EMNLP、IJCAI（部分方向）</li><li><strong>计算机视觉</strong>：ECCV、ACCV</li><li><strong>综合类</strong>：IEEE Transactions系列期刊（如TPAMI、TIT）</li></ul></li></ul><h4 id="_3-三类会议-期刊-入门级学术平台" tabindex="-1"><strong>3. 三类会议/期刊（入门级学术平台）</strong> <a class="header-anchor" href="#_3-三类会议-期刊-入门级学术平台" aria-label="Permalink to &quot;**3. 三类会议/期刊（入门级学术平台）**&quot;">​</a></h4><ul><li><strong>特点</strong>：区域性会议或影响因子较低的期刊，录用率较高（40%~60%），适合科研起步。</li><li><strong>举例</strong>：部分CCF-C类会议（如ISCAS）、EI索引的国际会议、SCI三四区期刊。</li></ul><h4 id="_4-其他类型" tabindex="-1"><strong>4. 其他类型</strong> <a class="header-anchor" href="#_4-其他类型" aria-label="Permalink to &quot;**4. 其他类型**&quot;">​</a></h4><ul><li><strong>Workshop/Poster</strong>：顶会的附属研讨会，适合初步成果交流。</li><li><strong>预印本平台</strong>：arXiv（计算机领域常用，可快速分享未正式发表的成果）。</li><li><strong>国内核心期刊</strong>：《计算机学报》《软件学报》等（部分高校认可，但国际影响力有限）。</li></ul><hr><h3 id="二、985高校计算机本硕研究生的发表层次" tabindex="-1"><strong>二、985高校计算机本硕研究生的发表层次</strong> <a class="header-anchor" href="#二、985高校计算机本硕研究生的发表层次" aria-label="Permalink to &quot;**二、985高校计算机本硕研究生的发表层次**&quot;">​</a></h3><h4 id="_1-本科生阶段" tabindex="-1"><strong>1. 本科生阶段</strong> <a class="header-anchor" href="#_1-本科生阶段" aria-label="Permalink to &quot;**1. 本科生阶段**&quot;">​</a></h4><ul><li><strong>常见情况</strong>：多数无论文发表，少数优秀学生通过科研训练或实验室参与发表： <ul><li><strong>顶会/期刊</strong>：极少数（如清华、北大等顶尖本科生参与重大项目）。</li><li><strong>二类会议/期刊</strong>：少数（如CCF-B类会议或SCI三区论文）。</li><li><strong>国内核心/国际会议Poster</strong>：较常见。</li></ul></li></ul><h4 id="_2-硕士阶段" tabindex="-1"><strong>2. 硕士阶段</strong> <a class="header-anchor" href="#_2-硕士阶段" aria-label="Permalink to &quot;**2. 硕士阶段**&quot;">​</a></h4><ul><li><strong>目标要求</strong>：国内985高校通常不强制要求论文，但申请顶尖博士项目需1~2篇优质论文。</li><li><strong>实际发表情况</strong>： <ul><li><strong>顶会/期刊</strong>：约10%~20%的优秀硕士生（尤其AI方向竞争激烈）。</li><li><strong>二类会议/期刊</strong>：主流选择（如CCF-B类会议或JCR二区期刊）。</li><li><strong>三类会议/期刊</strong>：用于毕业达标（部分高校认可EI会议或SCI四区论文）。</li></ul></li></ul><h4 id="_3-学科差异" tabindex="-1"><strong>3. 学科差异</strong> <a class="header-anchor" href="#_3-学科差异" aria-label="Permalink to &quot;**3. 学科差异**&quot;">​</a></h4><ul><li><strong>AI/机器学习</strong>：顶会竞争激烈，但投稿量大，硕士生发表顶会相对常见。</li><li><strong>系统/网络</strong>：顶会门槛高，更依赖工程能力和系统性贡献，硕士生发表较少。</li><li><strong>理论方向</strong>：论文周期长，硕士生通常以合作者身份参与。</li></ul><hr><h3 id="三、科研新手的实用建议" tabindex="-1"><strong>三、科研新手的实用建议</strong> <a class="header-anchor" href="#三、科研新手的实用建议" aria-label="Permalink to &quot;**三、科研新手的实用建议**&quot;">​</a></h3><ol><li><strong>明确研究方向</strong>：选择与导师课题匹配的方向，利用实验室资源积累经验。</li><li><strong>从小目标开始</strong>：从Workshop或二类会议起步，逐步提升研究深度。</li><li><strong>代码与实验</strong>：计算机领域重视可复现性，善用GitHub管理代码，提升技术扎实度。</li><li><strong>利用预印本</strong>：通过arXiv提前获取同行反馈，加速论文迭代。</li><li><strong>合作与交流</strong>：参与学术社群（如顶会讨论组），争取与博士生或博士后合作。</li></ol><hr><h3 id="四、总结" tabindex="-1"><strong>四、总结</strong> <a class="header-anchor" href="#四、总结" aria-label="Permalink to &quot;**四、总结**&quot;">​</a></h3><ul><li><strong>硕士生常见目标</strong>：以CCF-B类会议或二区期刊为主，冲刺顶会需长期积累。</li><li><strong>关键能力</strong>：问题定义、实验设计、论文写作（可参考《How to Write a Great Research Paper》等指南）。</li><li><strong>心态调整</strong>：论文被拒是常态，需持续改进研究质量而非单纯追求层次。</li></ul>',24)]))}const _=t(a,[["render",s]]);export{c as __pageData,_ as default};
