import{_ as i,c as e,o,ag as t}from"./chunks/framework.BmLMQRXF.js";const u=JSON.parse('{"title":"经验之谈","description":"","frontmatter":{},"headers":[],"relativePath":"coding/经验之谈.md","filePath":"coding/经验之谈.md"}'),r={name:"coding/经验之谈.md"};function a(n,l,s,g,p,A){return o(),e("div",null,l[0]||(l[0]=[t('<h1 id="经验之谈" tabindex="-1">经验之谈 <a class="header-anchor" href="#经验之谈" aria-label="Permalink to &quot;经验之谈&quot;">​</a></h1><h3 id="一、how-to-live-longer" tabindex="-1">一、How to live longer <a class="header-anchor" href="#一、how-to-live-longer" aria-label="Permalink to &quot;一、How to live longer&quot;">​</a></h3><p><strong>ACM</strong>: All-Cause Mortality / 全因死亡率</p><p><strong>目标</strong></p><ul><li>稳健地活得更久</li><li>更少的时间工作</li></ul><p><strong>关键结果</strong></p><ul><li>降低66.67%全因死亡率</li><li>增加20年预期寿命</li></ul><p><strong>分析</strong></p><ul><li>主要参考：对ACM的学术文献相对较多，可以作为主要参考</li><li>增加寿命与ACM关系非线性：显然增加寿命与ACM关系是非线性函数，这里假设 <code>ΔLifeSpan=(1/(1+ΔACM)-1)*10</code></li><li>变量无法简单叠加：显然各个变量之间并不符合独立同分布假设，变量之间的实际影响也并不明确</li><li>存在矛盾观点：所有的证据都有文献/研究对应，但注意到：有些文献之间有显著矛盾的观点（如对于碳水摄入比例的矛盾）；有些文献存在较大争议（如认为22点前睡觉会提升43%全因死亡率）</li><li>研究仅表达相关：所有文献表明的更多是相关而非因果，在阅读时要考虑文献是否充分证明了因果 —— 如某文献表明了日均&gt;=7000步的人有显著低的全因死亡率。但步数少的人可能包含更多长期病患，如果没有合理的排除这块数据，那此文献调查失真</li></ul><p><strong>行动</strong></p><ul><li>输入 <ul><li>固体：吃白肉（-11%~-3% ACM）、蔬果为主（-26%~-17% ACM），多吃辣（-23% ACM），多吃坚果（-27%~-4% ACM），中量碳水、多吃植物蛋白（-10% ACM），少吃超加工食物（-62%~-18%）</li><li>液体：喝咖啡（-22%~-12% ACM），喝牛奶（-17%~-10% ACM），喝茶（-15%~-8% ACM），少喝或不喝甜味饮料（否则每天一杯+7% ACM，+多巴胺），戒酒（否则+~50% ACM，无上限）</li><li>气体：不吸烟（否则+~~50% ACM，-12~~-11年寿命）</li><li>光照：晒太阳（-~40% ACM）</li><li>药物：二甲双胍（糖尿病人相比正常人可以+3年）、复合维生素（-8%癌症风险）、亚精胺（-60%~-30% ACM）、葡萄糖胺（-39% ACM）</li></ul></li><li>输出 <ul><li>运动：每周3次45分钟挥拍运动（-47% ACM）</li><li>日常：刷牙（-25% ACM）</li><li>睡眠：每天睡7小时全因死亡率最低；且22-24点间最好，<em>早睡+43% ACM，晚睡+15% ACM（存在争议）</em></li></ul></li><li>上下文 <ul><li>体重：减肥（-54% ACM）</li></ul></li></ul><p><strong>身体是革命的本钱！</strong></p><p><strong>转载自<a href="https://github.com/geekan/HowToLiveLonger" target="_blank" rel="noreferrer">https://github.com/geekan/HowToLiveLonger</a></strong></p>',13)]))}const _=i(r,[["render",a]]);export{u as __pageData,_ as default};
